class insertWiroPcCommand(sublime_plugin.TextCommand):
    def run(self, edit):
        start = "[wiro_pc]\n"
        end = "\n[/wiro_pc]"
        for region in self.view.sel():
            if self.view.substr(region).find("\n") == -1:
                start = "[wiro_pc]"
                end = "[/wiro_pc]"
            else:
                start = "[wiro_pc]\n"
                end = "\n[/wiro_pc]"
            self.view.replace(edit, region, start + self.view.substr(region) + end)

